import React, { useState } from "react";

const App = () => {
  const [item, setItem] = useState("");

  const itemList = (eve) => {
    setItem(eve.target.value);
  };
  return (
    <>
      <div className="main_div">
        <br />
        <div className="main">
          <h1>ToDo List</h1>
        </div>
        <input type="text" placeholder="Add an Items" onChange={itemList} />
        <button>+</button>
        <ul>
          <li>{item}</li>
        </ul>
      </div>
    </>
  );
};

export default App;
