import React, { useState } from "react";
//import * as List from "./temp";

const App = (eve) => {
  const [name, UpdtName] = useState(eve.target.value);

  const Update = (eve) => {
    UpdtName(eve.target.value);
  };

  return (
    <>
      <h1>Hello, {name}</h1>
      <input type="text" placeholder="Enter Your Name" onChange={Update} />
    </>
  );
};

export default App;
