import React, { useState } from "react";

const App = () => {
  const [item, setItem] = useState("");

  let AllItem = [];

  const itemList = (eve) => {
    setItem(eve.target.value);
  };

  const saveItem = () => {
    return AllItem.push(item);
  };
  return (
    <>
      <div className="main_div">
        <br />
        <div className="main">
          <h1>ToDo List</h1>
        </div>
        <input type="text" placeholder="Add an Items" onChange={itemList} />
        <button onClick={saveItem}>+</button>
        {AllItem.map((name) => (
          <li>{name}</li>
        ))}
      </div>
    </>
  );
};

export default App;
