import React, { useState } from "react";
//import * as List from "./temp";

const App = () => {
  const [fname, UpdtFname] = useState("");
  const [lname, UpdtLname] = useState("");
  const [email, UpdtEmail] = useState("");

  const Fname = (eve) => {
    UpdtFname(eve.target.value);
  };

  const Lname = (eve) => {
    UpdtLname(eve.target.value);
  };

  const Email = (eve) => {
    UpdtEmail(eve.target.value);
  };

  return (
    <>
      <h1>
        Hello, {fname} {lname}
      </h1>
      <h2>{email}</h2>
      <input type="text" placeholder="Enter Your fName" onChange={Fname} />
      <input type="text" placeholder="Enter Your lName" onChange={Lname} />
      <input type="text" placeholder="Enter Your E-mail" onChange={Email} />
    </>
  );
};

export default App;
