import React, { useState } from "react";

const App = () => {
  const [item, setItem] = useState("");
  const [arrItem, setArray] = useState([]);

  const itemList = (eve) => {
    setItem(eve.target.value);
  };

  const saveItem = () => {
    setArray((oldData) => {
      return [...oldData, item];
    });
  };
  return (
    <>
      <div className="main_div">
        <br />
        <div className="main">
          <h1>ToDo List</h1>
        </div>
        <input type="text" placeholder="Add an Items" onChange={itemList} />
        <button onClick={saveItem}>+</button>
        {arrItem.map((itemval) => {
          return <li>{itemval}</li>;
        })}
      </div>
    </>
  );
};

export default App;
