import React, { useState } from "react";

const App = () => {
  const [item, setItem] = useState("");
  const [arrItem, setArray] = useState([]);

  const itemList = (eve) => {
    setItem(eve.target.value);
  };

  const saveItem = () => {
    setArray((oldData) => {
      return [...oldData, item];
    });
    setItem("");
  };
  return (
    <>
      <div className="main_div">
        <br />
        <div className="main">
          <h1>ToDo List</h1>
        </div>
        <input
          type="text"
          placeholder="Add an Items"
          value={item}
          onChange={itemList}
        />
        <button onClick={saveItem}>+</button>
        <ol>
          {arrItem.map((itemval) => {
            return (
              <>
                {" "}
                <li>{itemval}</li>
                <hr />
              </>
            );
          })}
        </ol>
      </div>
    </>
  );
};

export default App;
